// Importing React to use JSX and create components
import React from 'react';

// Importing hooks from react-redux to interact with the Redux store
import { useSelector, useDispatch } from 'react-redux';

// Importing action creators to dispatch actions
import { increment, decrement } from '../redux/actions';
import Counter from './Counter';

// Defining the Counter component
const Counter2 = () => {
    // useSelector hook to access the current state from the Redux store
    const count2 = useSelector(state => state); // Access state directly

    // useDispatch hook to get the dispatch function to dispatch actions
    const dispatch = useDispatch();

    // Returning JSX to render the component
    return (
        <div>

            <h1>Other-Component</h1>
            {/* Button to dispatch the increment action */}
            <button onClick={() => dispatch(increment())}>Increment</button>
            {/* Button to dispatch the decrement action */}
            <button onClick={() => dispatch(decrement())}>Decrement</button>
        </div>
    );
};

